gameforge.goldbar._180_say = "Het was niet mogelijk om een goudstaaf te krijgen. "
gameforge.goldbar._190_say = "We moeten wachten op %s %s "
gameforge.goldbar._200_say = "Ik heb een goudstaaf bij me. Het kost 2,2 miljoen[ENTER]Yang. De 200.000 Yang is mijn betaling.[ENTER]Wat denk je ervan? Wil je het kopen? "
