gameforge.locale.monster_chat_1037_5 = "(gelach) Je kunt me zo niet doden! "
gameforge.locale.monster_chat_1037_6 = "Jaaaaaaaaa, dat is beter! "
gameforge.locale.monster_chat_1038_1 = "Dit is hel. "
gameforge.locale.monster_chat_1038_2 = "Welkom in hel. "
gameforge.locale.monster_chat_1038_3 = "Sterf alsjeblieft langzaam.... "
gameforge.locale.monster_chat_1038_4 = "Het heeft geen nut om me aan te vallen! (gelach) "
gameforge.locale.monster_chat_1038_5 = {}
gameforge.locale.monster_chat_1038_6 = "Het is niet goed om snel te sterven - we willen plezier met je maken eerst.... "
gameforge.locale.monster_chat_1039_1 = "Totdat iemand anders langskomt... "
gameforge.locale.monster_chat_1039_2 = "Dacht je echt dat je kon ontsnappen? "
gameforge.locale.monster_chat_1039_3 = "Alles hier maakt mensen bang; heel, heel, heel bang... "
gameforge.locale.monster_chat_1039_4 = "Welkom in ons huis (gelach). "
gameforge.locale.monster_chat_1039_5 = "Zie je? Je hebt gemist..... "
gameforge.locale.monster_chat_1039_6 = "Hmmm, zo vers.... "
gameforge.locale.monster_chat_1040_1 = "We zien al je bewegingen. "
gameforge.locale.monster_chat_1040_2 = "Doe het rustig aan. "
gameforge.locale.monster_chat_1040_3 = "Als je iemand aanraakt, ben je dood. (gelach) "
gameforge.locale.monster_chat_1040_4 = "Hoor je mijn bel luiden? Het is het laatste ding dat je zult horen! "
gameforge.locale.monster_chat_1040_5 = "Als de wond niet stopt met bloeden, zou het wel eens heel gevaarlijk voor je kunnen worden.... "
gameforge.locale.monster_chat_1040_6 = "Ja! Vecht, vecht! (gelach) "
gameforge.locale.monster_chat_1041_1 = "(gelach) (gelach) (gelach) "
gameforge.locale.monster_chat_1041_2 = "De nieuwe lijken zijn er. "
gameforge.locale.monster_chat_1041_3 = "Ja, het is tijd om nieuwe lichamen te halen. "
gameforge.locale.monster_chat_1041_4 = "We herrijzen. We kunnen niet dood! "
gameforge.locale.monster_chat_1041_5 = "Geen zin om ons aan te vallen! (gelach) "
gameforge.locale.monster_chat_1041_6 = "Wat zal je krijgen: vermoeidheid of de dood? "
gameforge.locale.monster_chat_1091_1 = "Wie kan beter vliegen en aanvallen dan ik? "
gameforge.locale.monster_chat_1091_2 = "Je kunt net als mij worden. "
gameforge.locale.monster_chat_1091_3 = "Wil je niet zoals mij zijn? "
gameforge.locale.monster_chat_1091_4 = "Je lichaam en ziel zijn de mijne! (gelach) "
gameforge.locale.monster_chat_1091_5 = "Vermoeid? Je benen trillen... "
gameforge.locale.monster_chat_1091_6 = "Hoe lang kun je zo door gaan? (gelach) "
gameforge.locale.monster_chat_1093_1 = "Wie had er kunnen verwachten dat er iets ergers is dan de dood? "
gameforge.locale.monster_chat_1093_2 = "Welkom, doodzoeker "
gameforge.locale.monster_chat_1093_3 = "Wees mijn prooi! "
gameforge.locale.monster_chat_1093_4 = "Ik zal smullen van je lichaam en ziel! "
gameforge.locale.monster_chat_1093_5 = "Hoe lang kun je het weerstaan? Huh? "
gameforge.locale.monster_chat_1093_6 = "De dood staat achter je. (gelach) "
gameforge.locale.monster_chat_11000_1 = "Ieder die bezorgd is om hun ontwikkeling moet luisteren! "
gameforge.locale.monster_chat_11000_10 = "Als je de kracht van natuur en bliksem wilt controleren, kies dan de traning in helende krachten! "
gameforge.locale.monster_chat_11000_11 = "Elke krijger is anders in zijn eigen manier van vechten! Focus op het versterken van je lichaam! "
gameforge.locale.monster_chat_11000_12 = "De kracht van de Sura is magie. Werk aan je intelligentie, snelheid en lichaams kracht! "
gameforge.locale.monster_chat_11000_13 = "Gebruik de vaardigheden van de ninja! Focuse op snelheid en lichaams kracht! "
gameforge.locale.monster_chat_11000_14 = "Sjamanen gebruiken regeneratie, ondersteunings- en gevechtsspreuken. Je moet zo intelligent mogelijk worden! "
gameforge.locale.monster_chat_11000_15 = "Vergeet je leerlingschap niet! "
gameforge.locale.monster_chat_11000_16 = "Wordt een leerling als je je vaardigheden wilt trainen! "
gameforge.locale.monster_chat_11000_17 = "Vaardigheden? Je kunt tot de rank van Meester stijgen, willekeurig verspreid tussen levels 17 en 23. "
gameforge.locale.monster_chat_11000_18 = "Wil je een Grootmeester worden? Dan heb je een extra vaardighedenboek nodig! "
gameforge.locale.monster_chat_11000_19 = "Als je een vaardighedenboek leest, zal het verdwijnen! "
gameforge.locale.monster_chat_11000_2 = "Iedereen die nog niet level 5 is, luister naar me! "
gameforge.locale.monster_chat_11000_20 = "Wil je echt opnieuw het vaardighedenboek lezen? Daarvoor moet je een dag wachten. "
gameforge.locale.monster_chat_11000_21 = "Verhoog je intelligentie om je Sura vaardigheden te verbeteren! "
gameforge.locale.monster_chat_11000_22 = "Ervaar de grote krachten van de Sura in gevecht! "
gameforge.locale.monster_chat_11000_23 = "Weet je waar de tempel van de mysterieuze sekte is? In het midden van de Seungryong vallei! "
gameforge.locale.monster_chat_11000_24 = "De spin grotten zijn weer verschenen! Ze zijn in de Yongbi woestijn! "
gameforge.locale.monster_chat_11000_25 = "Rond de missie af en gebruik de beloning voor verbetering. "
gameforge.locale.monster_chat_11000_26 = "Vissen? Klinkt goed! Heb je al een Hengel en wat Aas van de visser? "
gameforge.locale.monster_chat_11000_27 = "Zoek je iets sterkers om op te jagen? Zoek een portaal! Ga naar een jachtgebied geschikt voor jouw level! "
gameforge.locale.monster_chat_11000_28 = "Pas op voor vijanden uit andere Keizerrijken. Je weet maar nooit wanneer ze een mes in je rug steken. "
gameforge.locale.monster_chat_11000_29 = "Wil je je vaardigheden terugzetten? Ga dan naar het Oud Vrouwtje. Breng wel genoeg Yang mee! "
gameforge.locale.monster_chat_11000_3 = "Ga naar de leraar zwaardvechten als je te boek wilt staan als een groot offensief krijger! "
gameforge.locale.monster_chat_11000_30 = "Ga voor de perfecte meester rank! Dit is de enige manier om een echte held te worden! "
gameforge.locale.monster_chat_11000_31 = "Als je naar een andere gevechtsgebied of stad, vind dan oude mensen op het continent. "
gameforge.locale.monster_chat_11000_4 = "Als je bekwaam wilt zijn in de vaardigheden van de Krijger, ga dan naar de leraar in de oorlogskunst. "
gameforge.locale.monster_chat_11000_5 = "Als je een Sura wilt worden die een meester met magische zwaarden is, kies dan training wapenkunst! "
gameforge.locale.monster_chat_11000_6 = "Als je een Sura wilt worden die magische zwaarden meester is, kies dan magische - wapentraining! "
gameforge.locale.monster_chat_11000_7 = "De training boogschieten is gereserveerd voor ninja's die level 5 bereikt hebben. "
gameforge.locale.monster_chat_11000_8 = "Kies voor de training zwaardvechten als je de geheime ranglijst der ninja's wilt beklimmen. "
gameforge.locale.monster_chat_11000_9 = "Als je gevechten wilt controleren als een krijger van de Draken God, kies dan voor de training drakenkracht. "
