gameforge.locale.monster_chat_1105_1 = "Bevroren staat is de beste staat. "
gameforge.locale.monster_chat_1105_2 = "Berg Sohan is het einde! "
gameforge.locale.monster_chat_1105_3 = "Mooi weertje vandaag. "
gameforge.locale.monster_chat_1105_4 = "Je zult precies zoals mij zijn. "
gameforge.locale.monster_chat_1105_5 = "Je kunt voor eeuwig leven, althans totdat het ijs smelt. "
gameforge.locale.monster_chat_1105_6 = "Hoe denk je dat het voelt, als je langzaam in een stuk ijs veranderd? "
gameforge.locale.monster_chat_1106_1 = "Mensen...hier? "
gameforge.locale.monster_chat_1106_2 = "Slaperig.. maar wakker genoeg voor jou... "
gameforge.locale.monster_chat_1106_3 = "Is hier iemand, op mijn heuvel?  "
gameforge.locale.monster_chat_1106_4 = "Je hoort hier niet! "
gameforge.locale.monster_chat_1106_5 = "Je kunt maar beter weggaan! "
gameforge.locale.monster_chat_1106_6 = "Vreemdeling...zo eenzaam? "
gameforge.locale.monster_chat_1191_1 = "Laten we eens kijken of er knappe mannen voorbij komen. "
gameforge.locale.monster_chat_1191_2 = "Zo koud en eenzaam. Waar is mijn toekomstige man? "
gameforge.locale.monster_chat_1191_3 = "Zal ik op hen werken? "
gameforge.locale.monster_chat_1191_4 = {}
gameforge.locale.monster_chat_1191_5 = "Er mist iets. Ik ben niet geamuseerd. "
gameforge.locale.monster_chat_1191_6 = "Ik zal de laatste pijn die ooit zal voelen eindigen! "
gameforge.locale.monster_chat_1301_1 = "Een langnek! "
gameforge.locale.monster_chat_1301_2 = "Je bleke huid zal nog bleker worden in een minuut. "
gameforge.locale.monster_chat_1301_3 = "Je daagt me uit? Prima! "
gameforge.locale.monster_chat_1301_4 = "Je hebt me aangevallen! Pweck! Je mag niet overleven! "
gameforge.locale.monster_chat_1301_5 = "Zwak mens! "
gameforge.locale.monster_chat_1301_6 = "Laten we eens zien hoeveel je kan hebben! "
gameforge.locale.monster_chat_1302_1 = "Wegrennen? Denk er niet eens aan. "
gameforge.locale.monster_chat_1302_2 = "Kom... Kom maar dichterbij...Pwiehk! "
gameforge.locale.monster_chat_1302_3 = "De zwaard in mijn hand trilt. Pwiehk! "
gameforge.locale.monster_chat_1302_4 = "Je zult deze ontmoeting niet overleven! "
gameforge.locale.monster_chat_1302_5 = "Hier komt mijn zwaard! "
gameforge.locale.monster_chat_1302_6 = "Pijnlijk, is het niet? (gelach) "
gameforge.locale.monster_chat_1303_1 = "Zelfs gras waar je per ongeluk op stapt kan je doden. "
gameforge.locale.monster_chat_1303_2 = "Wees voorzichtig met het gras. "
gameforge.locale.monster_chat_1303_3 = "Goed. Kom bij me. (gelach) "
gameforge.locale.monster_chat_1303_4 = "Je durft het om zo dicht bij me te komen? Dan zul je leven met de gevolgen ervan! "
gameforge.locale.monster_chat_1303_5 = "Ga dood! Ga dood! "
gameforge.locale.monster_chat_1303_6 = "Heb je al spijt dat je bent gekomen? "
gameforge.locale.monster_chat_1305_1 = "Wie zal me verslaan? "
gameforge.locale.monster_chat_1305_2 = "Jij? Wil jij vechten? "
gameforge.locale.monster_chat_1305_3 = "Kom maar hier als je wilt! "
gameforge.locale.monster_chat_1305_4 = "Je denkt er niet over om weg te rennen, wel? "
gameforge.locale.monster_chat_1305_5 = "Je hebt onderschat hoe mijn zwaard bewegen kan. "
gameforge.locale.monster_chat_1305_6 = "Bang? (gelach) "
gameforge.locale.monster_chat_1901_1 = "Ik denk dat je aan mijn handen sterven zal. "
gameforge.locale.monster_chat_1901_2 = "Oh, ik vind je leuk.... "
gameforge.locale.monster_chat_1901_3 = "Wat denk je ervan? Mijn lieve afleiding... "
gameforge.locale.monster_chat_1901_4 = "Zeg! Ik kan je niet helpen als je wegrent als een lafaard! "
gameforge.locale.monster_chat_1901_5 = "Ben je al onder mijn betovering? Hmmm? "
gameforge.locale.monster_chat_1901_6 = "Wil je niet een klein beetje schreeuwen? (gelach) "
gameforge.locale.monster_chat_20001_1 = "(gelach) Je lijkt wel nerveus! Maak je geen zorgen, er zal niks ontploffen. "
gameforge.locale.monster_chat_20001_2 = "Soms, wil ik gewoon even wandelen. "
gameforge.locale.monster_chat_20001_3 = "Het is zo saai. Zelfs mijn kleinkinderen zoeken me niet op. "
gameforge.locale.monster_chat_20001_4 = "Wanneer je oud wordt, heb je andere mensen nodig. "
gameforge.locale.monster_chat_20001_5 = "Mijn beste dagen zijn voorbij. "
gameforge.locale.monster_chat_20002_1 = "De zon is te warm. "
gameforge.locale.monster_chat_20002_2 = "Heerlijk weer om een natte doek weer droog te krijgen, niet? "
gameforge.locale.monster_chat_20002_3 = "Waar is mijn luie goed-voor-niets man? "
gameforge.locale.monster_chat_20002_4 = "Noemt zichzelf een vader en schaamt zich niet eens voor zijn slecht gewoontes, zelfs voor de ogen van zijn kinderen! "
gameforge.locale.monster_chat_20002_5 = "Boeken zijn boeken. Hij verdient geen geld. Hij leest alleen boeken. "
gameforge.locale.monster_chat_20003_1 = "Oooh, dududu, baby. Heb je goed geslapen? "
gameforge.locale.monster_chat_20003_2 = "Je ruikt als een kleine engel. "
gameforge.locale.monster_chat_20003_3 = "Je moeder is zo blij dat je gezond bent. "
gameforge.locale.monster_chat_20003_4 = "Wil je weer aandacht? Heb je honger? "
gameforge.locale.monster_chat_20003_5 = "Lief kindje... slaap, mijn kind, slaap. "
gameforge.locale.monster_chat_20004_1 = "Gewoon een nieuwe dag. "
gameforge.locale.monster_chat_20004_2 = "Waar zal ik vandaag eten vandaan halen? "
gameforge.locale.monster_chat_20004_3 = "Altijd hetzelfde met mensen die zwaarden dragen. "
gameforge.locale.monster_chat_20004_4 = "Even denken. Misschien moet ik naar Octavio's restaurant gaan. "
gameforge.locale.monster_chat_20004_5 = "Oh trouwens, er schijnt geen oorlog aan de gang te zijn nu... "
gameforge.locale.monster_chat_20005_1 = "De ziel van de meester gaat door zijn vingertoppen. "
gameforge.locale.monster_chat_20005_2 = "Sinds het land chaotisch is geworden, kunnen krijgers overal gevonden worden. "
gameforge.locale.monster_chat_20005_3 = "Iedereen wil vechten. "
gameforge.locale.monster_chat_20005_4 = "Ik bid voor een kalm en vredig wereld. "
gameforge.locale.monster_chat_20005_5 = "Hey jij, ga even een minuutje relaxen en denken... "
gameforge.locale.monster_chat_20006_1 = "Oh nee, wat moet ik doen? "
gameforge.locale.monster_chat_20006_2 = "Ik kan niet eens de kleinst hint van mijn broer ontdekken, maar hij had beloofd dat hij hier zou zijn. "
gameforge.locale.monster_chat_20006_3 = "En nu, zie ik alleen maar krijgers met hun zwaarden. "
gameforge.locale.monster_chat_20006_4 = "Ik mis mijn broer. "
gameforge.locale.monster_chat_20006_5 = "Heeft iemand mijn broer gezien? "
gameforge.locale.monster_chat_20007_1 = "Hm, dat is echt vers vlees! "
gameforge.locale.monster_chat_20007_2 = "Het is goed dat mensen meer vlees eten. "
gameforge.locale.monster_chat_20007_3 = "Zaken gaan goed, maar het vlees is nog rauw. "
gameforge.locale.monster_chat_20007_4 = "Wanneer komt het vlees dat we besteld hebben? "
gameforge.locale.monster_chat_20007_5 = "Als het niet op tijd komt, moeten we gaan jagen. "
gameforge.locale.monster_chat_20008_1 = "Ik hoop dat de klanten in drommen komen wanneer het mooi weer wordt.... "
gameforge.locale.monster_chat_20008_2 = "De lui hond zal vast snel komen, als hij iets wilt eten. "
gameforge.locale.monster_chat_20008_3 = "Oh mijn hemel, wat een arme jongen! "
gameforge.locale.monster_chat_20008_4 = "Hij was ooit een groot krijger. "
gameforge.locale.monster_chat_20008_5 = "Ik vraag me af waarom hij niet meer vecht. "
gameforge.locale.monster_chat_20009_1 = "De jongemannen... Zo levendig en zo levendig. "
gameforge.locale.monster_chat_20009_2 = "De krijgers zien er stukken beter uit wanneer ze wapens dragen. "
gameforge.locale.monster_chat_20009_3 = "Tegenwoordig heeft iedereen een moordlustig verstand. "
gameforge.locale.monster_chat_20009_4 = "Controleer je hart, verdiep je in gedachten en kalmeer je ziel. "
gameforge.locale.monster_chat_20009_5 = "Er is geen onfeilbaar recept waardoor je sterker kan worden. Er zijn wel een aantal simpele richtlijnen.. "
gameforge.locale.monster_chat_20010_1 = "Ik heb je een fantastisch Voorwerp gebracht! "
gameforge.locale.monster_chat_20010_2 = "Goederen van verre oorden! Bloedmooie, bijna unieke objecten! "
gameforge.locale.monster_chat_20010_3 = "Kom hier! Iedereen, kom hier! "
gameforge.locale.monster_chat_20010_4 = "Kom op mensen, koop! Je zult nooit meer de kans krijgen! "
gameforge.locale.monster_chat_20010_5 = "Er zijn hier veel te veel dieven. Ze moeten een keer van mijn goederen afblijven. "
gameforge.locale.monster_chat_20011_1 = "Het is tegenwoordig zo droog... "
gameforge.locale.monster_chat_20011_2 = "De graan oogst is niet zo goed als het vroeger was. "
gameforge.locale.monster_chat_20011_3 = "Waarom bloeien de velden niet meer? "
gameforge.locale.monster_chat_20011_4 = "Het graan is breekbaar en smaakt verrot. "
gameforge.locale.monster_chat_20011_5 = "Dit is een slecht teken. Landbouw is de basis van ons leven. "
gameforge.locale.monster_chat_20012_1 = "Rijstkoeken! Heerlijke rijstkoeken! "
gameforge.locale.monster_chat_20012_2 = "Ik verkoop zoete rijstkoeken! "
gameforge.locale.monster_chat_20012_3 = "Ja, zaken gaan slecht tegenwoordig. "
gameforge.locale.monster_chat_20012_4 = "Misschien zou ik het in een andere stad moeten proberen. "
gameforge.locale.monster_chat_20012_5 = "Maar er zijn zoveel wilde roofdieren hier.... "
gameforge.locale.monster_chat_20013_1 = "Op dagen zoals deze, is op een boot zijn het beste wat je kan overkomen. "
gameforge.locale.monster_chat_20013_2 = "Perfect weer voor vissen! "
gameforge.locale.monster_chat_20013_3 = "Nog een knoop, klaar! Geen enkele vis kan deze haak weerstaan! "
gameforge.locale.monster_chat_20013_4 = "Stomme beer heeft mijn Hengel vernield! "
gameforge.locale.monster_chat_20013_5 = "Ik moet mijn net weer repareren. "
gameforge.locale.monster_chat_20014_1 = "Huh! "
gameforge.locale.monster_chat_20014_2 = "Maak je geen zorgen over me; zo ben ik nu eenmaal. "
gameforge.locale.monster_chat_20014_3 = "Haaaaa! "
gameforge.locale.monster_chat_20014_4 = "Wat moet ik doen zodat hij... Oh, wat zeg ik nu? "
gameforge.locale.monster_chat_20014_5 = "Ik kan helemaal niet moedig zijn. "
gameforge.locale.monster_chat_20015_1 = "Deze bijl heeft me goed gediend voor 20 jaar. "
gameforge.locale.monster_chat_20015_2 = "Nog steds scherp, maar niet zo goed als het eens was. "
gameforge.locale.monster_chat_20015_3 = "Ik wil het blijven gebruiken, dus moet het versterkt worden. "
gameforge.locale.monster_chat_20015_4 = "Maar ik heb stenen vol met mineralen daarvoor nodig. "
gameforge.locale.monster_chat_20015_5 = "Maar waar moet ik ze vandaan halen? "
gameforge.locale.monster_chat_20016_1 = "De klanten komen in drommen. "
gameforge.locale.monster_chat_20016_2 = "Ik hoop dat ik alles op tijd klaar heb. "
gameforge.locale.monster_chat_20016_3 = "Slag om slag en alsnog geen geklaag. "
gameforge.locale.monster_chat_20016_4 = "AH, zo moet een hamer klinken. "
gameforge.locale.monster_chat_20016_5 = "Vrij heet hier. "
gameforge.locale.monster_chat_20017_1 = "Ik heb geen zin om een instrument te bespelen. "
gameforge.locale.monster_chat_20017_2 = "Mijn instrument verkeert in een slechte staat. "
gameforge.locale.monster_chat_20017_3 = "Misschien moet ik een ander instrument kiezen "
gameforge.locale.monster_chat_20017_4 = "Mijn instrument is oud en versleten, daar houd ik wel van! "
gameforge.locale.monster_chat_20017_5 = "Hopelijk zal ik snel vervanging vinden voor deze snaren. "
gameforge.locale.monster_chat_20018_1 = "Ik heb het zo druk! "
gameforge.locale.monster_chat_20018_2 = "Elke dag, komen er meer gewonde mensen. "
gameforge.locale.monster_chat_20018_3 = "Geen tijd om uit te gaan en iemand te ontmoeten! "
gameforge.locale.monster_chat_20018_4 = "En we hebben bijna geen medicijn meer. "
gameforge.locale.monster_chat_20018_5 = "Ik wou dat iemand de ingrediÎnten kon halen voor het medicijn. "
gameforge.locale.monster_chat_20019_1 = "30 jaren lang dwaal ik al door de bergen met mijn boog. "
gameforge.locale.monster_chat_20019_2 = "Met dit heb ik mijn vrouw onderhouden en drie kinderen grootgebracht. "
gameforge.locale.monster_chat_20019_3 = "Het leven is zwaar maar het waard. "
gameforge.locale.monster_chat_20019_4 = "Maar nu is de boogsnaar kapot en ik kan niet meer jagen. Zo kan ik mijn familie niet onderhouden! "
gameforge.locale.monster_chat_20019_5 = "Als ik mijn boog niet kan repareren, moet ik wel een nieuwe kopen. Maar ik heb niet genoeg geld om dat te doen. "
gameforge.locale.monster_chat_20020_1 = "Waar kijk je naar? "
gameforge.locale.monster_chat_20020_2 = "Blijf hier vandaan! "
gameforge.locale.monster_chat_20020_3 = "Oh, hoe lang moet ik verstoppen? "
gameforge.locale.monster_chat_20020_4 = "Zal ik ergens sterven, helemaal vergeten? "
gameforge.locale.monster_chat_20020_5 = "Aaaah, ik hoop dat iemand mij wilt helpen... "
gameforge.locale.monster_chat_20021_1 = "Deze dag zal net als elke andere dag eindigen. "
gameforge.locale.monster_chat_20021_2 = "Wat kan ik nu doen, he? "
gameforge.locale.monster_chat_20021_3 = "Ik had nooit gedacht dat het zo zou eindigen. "
gameforge.locale.monster_chat_20021_4 = "Nee! Hij kan die de enige zijn die zo geÎindigd is! "
gameforge.locale.monster_chat_20021_5 = "Ik hoop dat hij gewroken word! "
gameforge.locale.monster_chat_20022_1 = "Weer een heerlijke dag. "
gameforge.locale.monster_chat_20022_2 = "Ariyoung's man is verdwenen. "
gameforge.locale.monster_chat_20022_3 = "Ik wilde helpen, maar deze koppigheid... "
gameforge.locale.monster_chat_20022_4 = "Als ze met me getrouwd was, had ze haar saaie leven achter zich kunnen laten. "
gameforge.locale.monster_chat_20022_5 = "Misschien had ik mijn gevoelens voor haar meer moeten laten zien. "
gameforge.locale.monster_chat_20023_1 = "Oh nee! Wat moet ik doen als mijn vrouw erachter komt? "
gameforge.locale.monster_chat_20023_2 = "Kon ik maar leven van boeken lezen! "
gameforge.locale.monster_chat_20023_3 = "Boeken zijn zo mysterieus. "
gameforge.locale.monster_chat_20023_4 = "Ik kan niet eeuwig zo leven maar... "
gameforge.locale.monster_chat_20023_5 = "Ik hoop dat ik iets voor mijn vrouw kan doen maar... "
gameforge.locale.monster_chat_20024_1 = "Kom op, kom op! Meer gevoel in je bewegingen! "
gameforge.locale.monster_chat_20024_2 = "Wat ben je aan het doen? Kun je niet opschieten? "
gameforge.locale.monster_chat_20024_3 = "Stel je eens een krijger voor, die zijn zwaard heft en doe hetzelfde. "
gameforge.locale.monster_chat_20024_4 = "Waar is de sukkel? Waar is hij nou? "
gameforge.locale.monster_chat_20024_5 = "Deze hond! Je bent geen man! Ha! "
gameforge.locale.monster_chat_2091_1 = "Ik knip, ik knip, of het nou haren of hoofden zijn... "
gameforge.locale.monster_chat_2091_2 = "Ik heb een cadeautje voor je: eeuwige rust "
gameforge.locale.monster_chat_2091_3 = "Adem maar eens diep in: het is slechts een beetje gif! "
gameforge.locale.monster_chat_2091_4 = "Ik zal alleen je gezicht overlaten als versiering! "
gameforge.locale.monster_chat_2091_5 = {}
gameforge.locale.monster_chat_2091_6 = "Met een krokant schilletje van zijde ben je een uitstekende snack voor later! "
gameforge.locale.monster_chat_2092_1 = "Er zijn niet veel over van onze soort. "
gameforge.locale.monster_chat_2092_2 = "Deze tweevoeters zijn niet de heersers van dit land! "
gameforge.locale.monster_chat_2092_3 = "Oh, lijkt erop dat iemand in een spinnenweb gevangen is. "
gameforge.locale.monster_chat_2092_4 = "Ik ben een pacifist, maar wee degene die me irriteert! "
gameforge.locale.monster_chat_2092_5 = "Is dit werkelijk alles dat je geven kan? "
gameforge.locale.monster_chat_2092_6 = "Niemand kan zich met mij meten! "
gameforge.locale.monster_chat_2104_1 = "Waarom zijn mensen zo nat van binnen? Daardoor gaat mijn zwaard roesten. "
gameforge.locale.monster_chat_2104_2 = "Is het gif in je angel al opgedroogd? "
gameforge.locale.monster_chat_2104_3 = "Hoe zit dat met het vechten? "
gameforge.locale.monster_chat_2104_4 = "Nu al vermoeid door de hitte? "
gameforge.locale.monster_chat_2104_5 = "Ik hou ervan om mensen achter te laten in de woestijn. "
gameforge.locale.monster_chat_2104_6 = "Arme jij, zo begraven in de woestijn. "
gameforge.locale.monster_chat_2105_1 = "Ik zal je een geheim verklappen - ik heb een hekel aan zand! "
gameforge.locale.monster_chat_2105_2 = "Het is zo warm, vind je niet? "
gameforge.locale.monster_chat_2105_3 = "Het is hier gevaarlijk. "
gameforge.locale.monster_chat_2105_4 = {}
gameforge.locale.monster_chat_2105_5 = "Ik ga je tussen de ogen slaan! "
gameforge.locale.monster_chat_2106_1 = "(zwaai) Zal ik vandaag iets krijgen? (zwaai) "
gameforge.locale.monster_chat_2106_2 = "Ooooooooh ja, hier komt er iemand aan om op te jagen. (gelach) (zwaai) "
gameforge.locale.monster_chat_2106_3 = "Dat vind ik leuk; dat vind ik heel leuk! "
gameforge.locale.monster_chat_2106_4 = "Die dikke is voor mij! "
gameforge.locale.monster_chat_2106_5 = {}
gameforge.locale.monster_chat_2106_6 = "Ik hou van de geur van bloed. "
gameforge.locale.monster_chat_2107_1 = "(zwaai) Niet veel te doen tegenwoordig (gelach) "
gameforge.locale.monster_chat_2107_2 = "Hey, die zijn echt dik! "
gameforge.locale.monster_chat_2107_3 = "De leider zal het geweldig vinden! "
gameforge.locale.monster_chat_2107_4 = {}
gameforge.locale.monster_chat_2107_5 = "Laten we onze benen bewegen. "
gameforge.locale.monster_chat_2107_6 = "Hier komt de pijl! "
gameforge.locale.monster_chat_2108_1 = "Als ik er een raak, dan... "
gameforge.locale.monster_chat_2108_2 = "Hey, waarom kijk je achter je? "
gameforge.locale.monster_chat_2108_3 = "Denk je echt dat ik zo makkelijk te verslaan ben? Hm? "
gameforge.locale.monster_chat_2108_4 = "Pech dat je me moest tegen komen in de woestijn! "
gameforge.locale.monster_chat_2108_5 = "Ah dit gevoel zal me eeuwig bijblijven! "
gameforge.locale.monster_chat_2108_6 = "Dank je! "
gameforge.locale.monster_chat_2191_1 = "Ik ruik mensen - zoooo smakelijk! "
gameforge.locale.monster_chat_2191_2 = "Goed, kom maar bij me, kom maar... "
gameforge.locale.monster_chat_2191_3 = {}
gameforge.locale.monster_chat_2191_4 = "Leuk om iemand te zien in de woestijn! "
gameforge.locale.monster_chat_2191_5 = "Sta stil! Aaaaa! "
gameforge.locale.monster_chat_2191_6 = "Laten we eerst een klein hapje nemen... "
gameforge.locale.monster_chat_2202_1 = "Ik snap echt niet waarom het zo heet moet zijn. "
gameforge.locale.monster_chat_2202_2 = "Was er maar iets koels... "
gameforge.locale.monster_chat_2202_3 = "Misschien kan ik afkoelen met hun bloed. "
gameforge.locale.monster_chat_2202_4 = "Heet, heet! Ik moet afkoelen met hun bloed. "
gameforge.locale.monster_chat_2202_5 = "Geef me je bloed! "
gameforge.locale.monster_chat_2202_6 = "Word net als mij! "
gameforge.locale.monster_chat_2204_1 = "Zolang ik hier ben, zal er niemand binnen gaan! "
gameforge.locale.monster_chat_2204_2 = "Je begint me enigszins te irriteren. "
gameforge.locale.monster_chat_2204_3 = {}
gameforge.locale.monster_chat_2204_4 = "Kwuuh, weet je waar je bent? "
gameforge.locale.monster_chat_2204_5 = {}
gameforge.locale.monster_chat_2204_6 = "Zo belachelijk mager! (gelach) "
gameforge.locale.monster_chat_2205_1 = "Een zwaard is alleen nuttig in de handen van een krijger. "
gameforge.locale.monster_chat_2205_2 = "Hou de zwaard in je hand. "
gameforge.locale.monster_chat_2205_3 = "Oorlog is op komst. "
gameforge.locale.monster_chat_2205_4 = "En nu, vechten we totdat iemand doodgaat! "
gameforge.locale.monster_chat_2205_5 = "Wil je de dood langer weerstaan? "
gameforge.locale.monster_chat_2205_6 = "Ja, je moet niet te snel sterven. "
gameforge.locale.monster_chat_2206_1 = "Het is zo lekker om in lava te zwemmen. "
gameforge.locale.monster_chat_2206_2 = "Ik hou ervan om mezelf te baden in verse lava! "
gameforge.locale.monster_chat_2206_3 = "Lava is niet meer wat het vroeger was.. "
gameforge.locale.monster_chat_2206_4 = "Jij schoft je hebt mijn rust verstoord! "
gameforge.locale.monster_chat_2206_5 = "Verstoor mijn rust niet. "
gameforge.locale.monster_chat_2206_6 = "Als je het doet, zal je het bezuren... "
gameforge.locale.monster_chat_2291_1 = "Lekker weer. Het zou zo fijn zijn om te slapen. "
gameforge.locale.monster_chat_2291_2 = "Niets is beter dan het land van Doyyumhwan. "
gameforge.locale.monster_chat_2291_3 = "Waarom ga ik niet slapen? "
gameforge.locale.monster_chat_2291_4 = "Je hebt me gestoord. "
gameforge.locale.monster_chat_2291_5 = "Je lijkt wel spijtig dat je me gestoord hebt. "
gameforge.locale.monster_chat_2291_6 = "Te laat nu om weg te rennen. En het zou je toch niet helpen. "
gameforge.locale.monster_chat_301_1 = "Ik verveel me. Is er niets interessants te doen. "
gameforge.locale.monster_chat_301_2 = "Twee dagen geleden, was hier een knappe man. "
gameforge.locale.monster_chat_301_3 = "Laten we onze haren knippen, mijn zusters! "
gameforge.locale.monster_chat_301_4 = "Geen probleem! Hehe, er goed uit zien is niet helemaal genoeg. "
gameforge.locale.monster_chat_301_5 = "Wil je mijn zwaard? We maken geen grappen. "
gameforge.locale.monster_chat_302_1 = "Hey! Das een knappe vent! "
gameforge.locale.monster_chat_302_2 = "Weet je, ik hou me aan een strikt dieet. "
gameforge.locale.monster_chat_302_3 = "Ik wil gaan winkelen. "
gameforge.locale.monster_chat_302_4 = "Dat was mijn pijl! "
gameforge.locale.monster_chat_302_5 = "Zie je? Het is op je hart gericht. "
gameforge.locale.monster_chat_302_6 = "Oh!! In de roos! "
gameforge.locale.monster_chat_303_1 = "Tegenwoordig zijn onze kinderen te lui. "
gameforge.locale.monster_chat_303_2 = "Wat zou er gebeuren als er plotseling soldaten binnenstormen? "
gameforge.locale.monster_chat_303_3 = "Waarom kijk je zo naar me? "
gameforge.locale.monster_chat_303_4 = "Oh, wat een lafaard! "
gameforge.locale.monster_chat_303_5 = "Doe het niet te rustig aan. "
gameforge.locale.monster_chat_303_6 = "Dit gevecht zal moeilijker zijn dan ik dacht. "
gameforge.locale.monster_chat_304_1 = "Jullie vrouwen sloten je aan toen jullie mannen weggingen. Je verlangt nog steeds naar mannen. "
gameforge.locale.monster_chat_304_2 = "Er zijn geen goede mannen. "
gameforge.locale.monster_chat_304_3 = "Je werkt op me zenuwen. "
gameforge.locale.monster_chat_304_4 = "Hoe voelt het? Kun je niet zien dat dood je in het gezicht staart? "
gameforge.locale.monster_chat_304_5 = "Oh nee! Geef je nu al op? "
gameforge.locale.monster_chat_304_6 = "Lijden komt snel. "
gameforge.locale.monster_chat_305_1 = "Oh man! Die zwaard ziet er geweldig uit. "
gameforge.locale.monster_chat_305_2 = "Wees aanhoudend en versterk je kracht! "
gameforge.locale.monster_chat_305_3 = "Denk je dat je zo sterk als mij kunt worden? "
gameforge.locale.monster_chat_305_4 = "Ok, laten we serieus doen. "
gameforge.locale.monster_chat_305_5 = "Oh nee! Ik ben maar een zwakke vrouw! "
gameforge.locale.monster_chat_305_6 = "Je ziet er moe uit. Waarom geef je het niet gewoon op. Hehe. "
gameforge.locale.monster_chat_306_1 = "Wat gaat Serangyi vandaag doen, hmm? "
gameforge.locale.monster_chat_306_2 = "Ik ben zo verveeld. Speel met me! "
gameforge.locale.monster_chat_306_3 = "Ik moet zeggen dat ik Sura mannen het leukst vind. "
gameforge.locale.monster_chat_306_4 = "Hey! Wat dacht je van een gevecht? "
gameforge.locale.monster_chat_306_5 = "Onderschat me niet alleen maar omdat ik een vrouw ben! "
gameforge.locale.monster_chat_306_6 = "Pijn duurt slechts een moment. "
gameforge.locale.monster_chat_307_1 = "Ik wil hier weg tegen elke prijs. "
gameforge.locale.monster_chat_307_2 = "Hoe veilig zijn de huizen van de individuele leden? "
gameforge.locale.monster_chat_307_3 = "Hou je mond meisjes! "
gameforge.locale.monster_chat_307_4 = "Laat me je verslaan met een enkele klap. "
gameforge.locale.monster_chat_307_5 = "Denk er niet aan om weg te rennen! "
gameforge.locale.monster_chat_307_6 = "Dus, waarom beginnen we niet? "
gameforge.locale.monster_chat_394_1 = "Vergeet niet: alle mannen zijn vijanden. "
gameforge.locale.monster_chat_394_2 = "Als iemand een leuk gezichtje heeft, kunnen we minder streng zijn. "
gameforge.locale.monster_chat_394_3 = "Wat, wil je vechten? "
gameforge.locale.monster_chat_394_4 = "Het zal niet lang duren, maar het gaat pijn doen. "
gameforge.locale.monster_chat_394_5 = "Oh, het lijkt alsof je echt pijn hebt. "
gameforge.locale.monster_chat_394_6 = "Vaarwel; ga naar de hel! "
gameforge.locale.monster_chat_401_1 = "Dus, wat wil je doen in de Witte Eed? "
gameforge.locale.monster_chat_401_2 = "Waar ga je heen? "
gameforge.locale.monster_chat_401_3 = "Hey, wil je vechten? "
gameforge.locale.monster_chat_401_4 = "Hehe, je ontwijkt al mijn aanvallen. "
gameforge.locale.monster_chat_401_5 = "Denk je dat je van me weg kunt rennen? "
gameforge.locale.monster_chat_401_6 = "Hier komt mijn slag! "
gameforge.locale.monster_chat_402_1 = "Oh, Ik ben zo moe... "
gameforge.locale.monster_chat_402_2 = "Hey, leuke pantser! Wat heeft het gekost? "
gameforge.locale.monster_chat_402_3 = "Maar we hebben wel mooi weer. "
gameforge.locale.monster_chat_402_4 = "Oh, je hebt veel getraind! Fantastisch! "
gameforge.locale.monster_chat_402_5 = "Wat denk je van mijn vaardigheden? Goed he? Hehe. "
gameforge.locale.monster_chat_402_6 = "Wat is er? Natuurlijk, het is de waanzinnige snelheid! "
gameforge.locale.monster_chat_403_1 = "Waarom ga ik niet jagen? "
gameforge.locale.monster_chat_403_2 = "Niemand valt ons dorp aan vandaag. "
gameforge.locale.monster_chat_403_3 = "Als je wilt vechten, vecht dan, jij schoft! "
gameforge.locale.monster_chat_403_4 = "Hehe, je bent slechts een beginner. "
gameforge.locale.monster_chat_403_5 = "Wat moet ik eerst slaan, je arm of je been? "
gameforge.locale.monster_chat_403_6 = "Hier komt mijn pijl! "
gameforge.locale.monster_chat_404_1 = "Ik moet het dorp in en trainen binnenkort. "
gameforge.locale.monster_chat_404_2 = "Ik voel me zo stijf. "
gameforge.locale.monster_chat_404_3 = "Wat is er te eten vandaag? "
gameforge.locale.monster_chat_404_4 = "Ik kijk toe, dus schreeuw zo veel je wilt. "
gameforge.locale.monster_chat_404_5 = "Te lang gewacht? Wat dacht je van dat zwaard van je zwaaien? "
gameforge.locale.monster_chat_404_6 = "De zwaard klinkt opgewonden! "
gameforge.locale.monster_chat_405_1 = "De zwaard is gereed! "
gameforge.locale.monster_chat_405_2 = "Laten we eens zien wie er als eerste gedood wordt door dit zwaard. "
gameforge.locale.monster_chat_405_3 = "Hey, waar kijk je naar? "
gameforge.locale.monster_chat_405_4 = "Eerst, je buik! "
gameforge.locale.monster_chat_405_5 = "Ik zou mijn mes graag in je willen bewaren. "
gameforge.locale.monster_chat_405_6 = "Ik zou mijn mes graag in je willen bewaren. "
gameforge.locale.monster_chat_406_1 = "Ik verhonger zowat. "
gameforge.locale.monster_chat_406_2 = "Is er iets te eten? "
gameforge.locale.monster_chat_406_3 = "Waarom heb ik nog honger na al dat eten? "
gameforge.locale.monster_chat_406_4 = "Aaargh, sterf! "
