gameforge.locale.monster_chat_491_1 = "Ik kan het niet meer aan! Mijn lichaam jeukt. "
gameforge.locale.monster_chat_491_2 = "Oh ja. Ik zie wat varkens. "
gameforge.locale.monster_chat_491_3 = "Op de grond? "
