gameforge.locale.monster_chat_491_6 = "Sterf! "
gameforge.locale.monster_chat_492_1 = "Mijn lichaam is verzacht. "
gameforge.locale.monster_chat_492_2 = "Waarom oefen ik niet meer? "
gameforge.locale.monster_chat_492_3 = "Bang, hmm? "
gameforge.locale.monster_chat_492_4 = "Ah, gewoon een nieuw slachtoffer voor mijn wervelend wapen. "
gameforge.locale.monster_chat_492_5 = "Heb je mijn aanvalspatroon al doorzien? Nee? Dan ben je ten dode opgeschreven! "
gameforge.locale.monster_chat_492_6 = "Wist je dat zelfs de kleinste snee niet goed voor je gezondheid is? "
gameforge.locale.monster_chat_493_1 = "Het is zo oncomfortabel om zo'n hand te hebben! "
gameforge.locale.monster_chat_493_2 = "Waar kijk je naar? Nooit iemand met zo'n hand gezien? "
gameforge.locale.monster_chat_493_3 = "Jullie irriteren me echt. "
gameforge.locale.monster_chat_493_4 = "Wil je me in elkaar slaan? Zo niet, ren dan weg. Begrepen? "
gameforge.locale.monster_chat_493_5 = "Ik zal je mijn grote slag laten zien! "
gameforge.locale.monster_chat_493_6 = "Ik weet dat je naar mijn hand staart! "
gameforge.locale.monster_chat_494_1 = "Hey jij, als je wilt vechten, kom dan hier! "
gameforge.locale.monster_chat_494_2 = "Ik denk dat ik aardig adembenemend ben. "
gameforge.locale.monster_chat_494_3 = "Waarom plannen we niet iets? "
gameforge.locale.monster_chat_494_4 = "Nee, je mag nog niet weg. Vecht! "
gameforge.locale.monster_chat_494_5 = "Daar heb je het! "
gameforge.locale.monster_chat_494_6 = "Je bent bijna dood! "
gameforge.locale.monster_chat_501_1 = "Speel met me! Ga niet weg! "
gameforge.locale.monster_chat_501_2 = "Ik denk dat ik best wat leuks in je botten kan kerven. "
gameforge.locale.monster_chat_501_3 = "We hebben nog niet geoefend. "
gameforge.locale.monster_chat_501_4 = "Wat een mooie pantser! Ik wil het hebben. "
gameforge.locale.monster_chat_501_5 = "Vanaf nu, zul je het moeilijker vinden om mijn aanvallen te negeren. "
gameforge.locale.monster_chat_501_6 = "Hehe, verbaasd? "
gameforge.locale.monster_chat_502_1 = "Ach, de schoonheidsprijs zal ik nooit winnen. "
gameforge.locale.monster_chat_502_2 = "Niet staren, vecht! "
gameforge.locale.monster_chat_502_3 = "Ik hou er niet van hoe je naar me staart. Vecht! "
gameforge.locale.monster_chat_502_4 = "Het is een eer om te sterven door mijn zwaard! "
gameforge.locale.monster_chat_502_5 = {}
gameforge.locale.monster_chat_502_6 = "Uitstekend! "
gameforge.locale.monster_chat_503_1 = "Hey daar, niet aanraken! "
gameforge.locale.monster_chat_503_2 = "Ziet ernaar uit dat mijn boogsnaar kapot is. "
gameforge.locale.monster_chat_503_3 = "Verdikkeme, die idioten waren hier. "
gameforge.locale.monster_chat_503_4 = "Niet slecht. Maar je gaat toch sterven! "
gameforge.locale.monster_chat_503_5 = "Ben je hier ok mee? Fantastisch! "
gameforge.locale.monster_chat_503_6 = "Hier komt de pijl! "
gameforge.locale.monster_chat_504_1 = "Iedereen, kom hier! "
gameforge.locale.monster_chat_504_2 = "Laat me toch lekker fluiten man! "
gameforge.locale.monster_chat_504_3 = "Wat een prachtige weer! "
gameforge.locale.monster_chat_504_4 = {}
gameforge.locale.monster_chat_504_5 = "Nou, ik vind je bereidheid om te vechten wel leuk! "
gameforge.locale.monster_chat_504_6 = "Iedereen, verzamel bij mij! "
gameforge.locale.monster_chat_601_1 = "Ik verhonger! "
gameforge.locale.monster_chat_601_2 = "Ik wil wat vruchten eten. "
gameforge.locale.monster_chat_601_3 = "Vooruit, met vis kan ik wel leven. Ten aanval! "
gameforge.locale.monster_chat_601_4 = "Niet weg rennen! Blijf hier! "
gameforge.locale.monster_chat_601_5 = "Ik heb gewonnen. Het is zo duidelijk! "
gameforge.locale.monster_chat_601_6 = "Zie je? Je hebt het gemist! "
gameforge.locale.monster_chat_601_7 = "HÈ, let eens op! "
gameforge.locale.monster_chat_602_1 = "Waar ben ik? "
gameforge.locale.monster_chat_602_2 = "Lekker weer. "
gameforge.locale.monster_chat_602_3 = "Je bent gekomen om op mij te jagen? Absurd! "
gameforge.locale.monster_chat_602_4 = "Je bent mijn prooi. "
gameforge.locale.monster_chat_602_5 = "Marteling, is het niet? Ik zal je in een ogenblik afmaken. "
gameforge.locale.monster_chat_602_6 = "Ik ben heel erg sterk, zoals je kan zien: ik kan deze gigantische knuppel rondzwaaien! "
gameforge.locale.monster_chat_603_1 = "Saai! "
gameforge.locale.monster_chat_603_2 = "Mijn hele lichaam jeukt. "
gameforge.locale.monster_chat_603_3 = "Wie er als eerste is. "
gameforge.locale.monster_chat_603_4 = "Mij als eerste slaan? Ik ga je hier vandaan drijven! "
gameforge.locale.monster_chat_603_5 = "Wanneer je binnen bent, kun je niet meer weg. Geen ontsnapping! "
gameforge.locale.monster_chat_603_6 = "Dit zal de laatste slag zijn. Ik zal je afmaken! "
gameforge.locale.monster_chat_604_1 = "Ik heb een overheerlijk nieuw gif gemaakt, wil je het proberen? "
gameforge.locale.monster_chat_604_2 = "Ik wil zien hoe het werkt, dus neem het mee! "
gameforge.locale.monster_chat_604_3 = "HÈ jij daar, kom eens hier! Ik heb een testpersoon nodig! "
gameforge.locale.monster_chat_604_4 = "Ik zal je vergiftigen! "
gameforge.locale.monster_chat_604_5 = "Oh prachtig, een proefdier! "
gameforge.locale.monster_chat_604_6 = "Nee, het is niet leuk als je zo snel opgeeft! Verdedig jezelf eens! "
gameforge.locale.monster_chat_631_1 = "Een moment. Ik ben aan het jagen. "
gameforge.locale.monster_chat_631_2 = "Het werkt niet als je zo langzaam bent! Beweeg eens wat! "
gameforge.locale.monster_chat_631_3 = "Ik heb zo een honger! "
gameforge.locale.monster_chat_631_4 = "Het is goed dat ik iets gevonden heb om op te jagen! "
gameforge.locale.monster_chat_631_5 = "Laat alle hoop op overleven varen! Dit overleef je niet! "
gameforge.locale.monster_chat_631_6 = "Ik zal je laten voelen wat echte pijn is! "
gameforge.locale.monster_chat_632_1 = "Muh, saai. "
gameforge.locale.monster_chat_632_2 = "Is er iets bruikbaar als aas? "
gameforge.locale.monster_chat_632_3 = "Kom en vecht als je wilt! "
gameforge.locale.monster_chat_632_4 = "Als je een krijger bent, laat dan maar zien wat je kunt! "
gameforge.locale.monster_chat_632_5 = {}
gameforge.locale.monster_chat_632_6 = "Ontsnap uit het dorp als je wilt leven! "
gameforge.locale.monster_chat_633_1 = "Het is pijnlijk om te verhongeren. "
gameforge.locale.monster_chat_633_2 = "Oh, ze komen recht op ons af! "
gameforge.locale.monster_chat_633_3 = "Wat dacht je van een duel? "
gameforge.locale.monster_chat_633_4 = "Vergelijk me niet met deze schoften! "
gameforge.locale.monster_chat_633_5 = "Hier komt de snelste slag van de wereld, harder dan de bliksem! "
gameforge.locale.monster_chat_633_6 = "Echte krijgers zullen nooit wegennen! "
gameforge.locale.monster_chat_634_1 = "Dit keer, is het gif better! "
gameforge.locale.monster_chat_634_2 = "Als je honger hebt, smaken vleermuizen opeens heel lekker. "
gameforge.locale.monster_chat_634_3 = "Wat dacht je van een gevecht? "
gameforge.locale.monster_chat_634_4 = "Wanneer mijn gif je lichaam binnendringt, is er geen manier meer om het te helen. "
gameforge.locale.monster_chat_634_5 = "Haga, mijn magie zal alles verbranden. "
gameforge.locale.monster_chat_634_6 = "Branden zal je! "
gameforge.locale.monster_chat_635_1 = "Vergeet nooit je zwaard te slijpen! Gevechten komen altijd eerder dan verwacht! "
gameforge.locale.monster_chat_635_2 = "Blackout? "
gameforge.locale.monster_chat_635_3 = "Sterf, zodat je ontwaakt in het hiernamaals! "
gameforge.locale.monster_chat_635_4 = "Mij verslaan? Echt? "
gameforge.locale.monster_chat_635_5 = "Vecht als een krijger! "
gameforge.locale.monster_chat_635_6 = "Kom op, nog een klein stukje. "
gameforge.locale.monster_chat_636_1 = "Ha, ik kijk uit naar het gevecht! "
gameforge.locale.monster_chat_636_2 = "Vechten is leuk. (gelach) "
gameforge.locale.monster_chat_636_3 = "Ik wil altijd vechten! "
gameforge.locale.monster_chat_636_4 = "Goed gedaan! Probeer het nog eens. "
gameforge.locale.monster_chat_636_5 = "Kijk naar mijn draaiende zwaard! "
gameforge.locale.monster_chat_636_6 = "Je zal het bezuren dat je me geslagen hebt! "
gameforge.locale.monster_chat_636_7 = "Zo zwak! Hoe lachwekkend! "
gameforge.locale.monster_chat_637_1 = "Ik wil niet gestoord worden. "
gameforge.locale.monster_chat_637_2 = "Ga maar slapen. Voor altijd! "
gameforge.locale.monster_chat_637_3 = "Nog steeds niet aan het slapen? "
gameforge.locale.monster_chat_637_4 = "Er is zoveel te doen, dus laten we opschieten! "
gameforge.locale.monster_chat_637_5 = "Wees niet verbaasd om mijn kracht! "
gameforge.locale.monster_chat_637_6 = "Ik ga je vermorzelen! "
gameforge.locale.monster_chat_691_1 = "Geweldig, sterke tegenstanders geven langere en betere gevechten! "
